static void FTM2_init(void) {
/****************************************************************************/
/* FTM peripheral global and channel modes initialization */
/****************************************************************************/
  if((FTM2->FMS & FTM_FMS_WPEN_MASK) != 0U)
  {
	/* Disable write protection */
    FTM2->MODE |= FTM_MODE_WPDIS_MASK;	
  }	
#ifdef FTM2_POL_INIT
  FTM2->POL = FTM2_POL_INIT & FTM2_OUT_INIT_MASK;
#else
  FTM2->POL = 0x0U;
#endif /*FTM2_POL_INIT*/
  /* Mask the channel output before the channel init. */
  FTM2->SYNC &= ~FTM_SYNC_SYNCHOM_MASK;
  FTM2->OUTMASK = FTM2_OUT_INIT_MASK;
  /* Disable the FTM counter */
  FTM2->SC = 0x0U;
  /* Disable the Quadrature mode first if select it */
#ifdef FTM2_QDCTRL_INIT
  FTM2->QDCTRL = 0x0U;
#endif /*FTM2_QDCTRL_INIT*/
#ifdef FTM2_MOD_INIT
  FTM2->MOD = FTM2_MOD_INIT;
#else
  FTM2->MOD = 0x0U;
#endif /*FTM2_MOD_INIT*/
#ifdef FTM2_CNTIN_INIT
  FTM2->CNTIN = FTM2_CNTIN_INIT;
#else
  FTM2->CNTIN = 0x0U;
#endif /*FTM2_CNTIN_INIT*/
#ifdef FTM2_COMBINE_INIT
  FTM2->COMBINE = FTM2_COMBINE_INIT & FTM2_COMBINE_INIT_MASK;
#else
  FTM2->COMBINE = 0x0U;
#endif /*FTM2_COMBINE_INIT*/
#ifdef FTM2_SC_INIT
  FTM2->SC = FTM2_SC_INIT & (~FTM_SC_CLKS_MASK);
#endif /*FTM2_SC_INIT*/
/****************************************************************************/
/* Channel 0 initialization */
/****************************************************************************/
#ifdef FTM2_C0SC_INIT
  FTM2->CONTROLS[0].CnSC = FTM2_C0SC_INIT;
#else
  FTM2->CONTROLS[0].CnSC = 0x0U;
#endif /*FTM2_C0SC_INIT */
#ifdef FTM2_C0V_INIT
  FTM2->CONTROLS[0].CnV = FTM2_C0V_INIT;
#else
  FTM2->CONTROLS[0].CnV = 0x0U;
#endif /*FTM2_C0V_INIT */

/****************************************************************************/
/* Channel 1 initialization */
/****************************************************************************/
#ifdef FTM2_C1SC_INIT
  FTM2->CONTROLS[1].CnSC = FTM2_C1SC_INIT;
#else
  FTM2->CONTROLS[1].CnSC = 0x0U;
#endif /*FTM2_C1SC_INIT */
#ifdef FTM2_C1V_INIT
  FTM2->CONTROLS[1].CnV = FTM2_C1V_INIT;
#else
  FTM2->CONTROLS[1].CnV = 0x0U;
#endif /*FTM2_C1V_INIT */

/****************************************************************************/
/* FTM peripheral global initialization */
/****************************************************************************/
#ifdef FTM2_MODE_INIT
  FTM2->MODE = FTM2_MODE_INIT & (~FTM_MODE_INIT_MASK);
#else
  FTM2->MODE = 0x0U;
#endif /*FTM2_MODE_INIT*/
#ifdef FTM2_DEADTIME_INIT
  FTM2->DEADTIME = FTM2_DEADTIME_INIT;
#else
  FTM2->DEADTIME = 0x0U;
#endif /*FTM2_DEADTIME_INIT*/
#ifdef FTM2_FLTCTRL_INIT
  FTM2->FLTCTRL = FTM2_FLTCTRL_INIT;
#else
  FTM2->FLTCTRL = 0x0U;
#endif /*FTM2_FLTCTRL_INIT*/
#ifdef FTM2_FLTPOL_INIT
  FTM2->FLTPOL = FTM2_FLTPOL_INIT;
#else
  FTM2->FLTPOL = 0x0U;
#endif /*FTM2_FLTPOL_INIT*/
#ifdef FTM2_EXTTRIG_INIT
  FTM2->EXTTRIG = FTM2_EXTTRIG_INIT;
#else
  FTM2->EXTTRIG = 0x0U;
#endif /*FTM2_EXTTRIG_INIT*/
#ifdef FTM2_FILTER_INIT
  FTM2->FILTER = FTM2_FILTER_INIT & FTM2_FILTER_INIT_MASK;
#else
  FTM2->FILTER = 0x0U;
#endif /*FTM2_FILTER_INIT*/
  /* Reset the FTM counter */
  FTM2->CNT = 0x1U;
#ifdef FTM2_QDCTRL_INIT
  FTM2->QDCTRL = FTM2_QDCTRL_INIT;
#else
  FTM2->QDCTRL = 0x0U;
#endif /*FTM2_QDCTRL_INIT*/
#ifdef FTM2_CONF_INIT
  FTM2->CONF = FTM2_CONF_INIT;
#else
  FTM2->CONF = 0x0U;
#endif /*FTM2_CONF_INIT*/
#ifdef FTM2_PWMLOAD_INIT
  FTM2->PWMLOAD = FTM2_PWMLOAD_INIT;
#else
  FTM2->PWMLOAD = 0x0U;
#endif /*FTM2_PWMLOAD_INIT*/
#ifdef FTM2_SYNC_INIT
  FTM2->SYNC = FTM2_SYNC_INIT;
#else
  FTM2->SYNC = 0x0U;
#endif /*FTM2_SYNC_INIT*/
#ifdef FTM2_SYNCONF_INIT
  FTM2->SYNCONF = FTM2_SYNCONF_INIT;
#else
  FTM2->SYNCONF = 0x0U;
#endif /*FTM2_SYNCONF_INIT*/
#ifdef FTM2_SWOCTRL_INIT
  FTM2->SWOCTRL = FTM2_SWOCTRL_INIT & (FTM2_OUT_INIT_MASK | (FTM2_OUT_INIT_MASK << FTM_SWOCTRL_CH0OCV_SHIFT));
#else
  FTM2->SWOCTRL = 0x0U;
#endif /*FTM2_SWOCTRL_INIT*/
#ifdef FTM2_INVCTRL_INIT
  FTM2->INVCTRL = FTM2_INVCTRL_INIT & FTM2_INVCTRL_INIT_MASK;
#else
  FTM2->INVCTRL = 0x0U;
#endif /*FTM2_INVCTRL_INIT*/
#ifdef FTM2_OUTINIT_INIT
  FTM2->OUTINIT = FTM2_OUTINIT_INIT & FTM2_OUT_INIT_MASK;
#else
  FTM2->OUTINIT = 0x0U;
#endif /*FTM2_OUTINIT_INIT*/
#ifdef FTM2_OUTMASK_INIT
  FTM2->OUTMASK = FTM2_OUTMASK_INIT & FTM2_OUT_INIT_MASK;
#else
  FTM2->OUTMASK = 0x0U;
#endif /*FTM2_OUTMASK_INIT*/
/* Initialize channel outputs */
#ifdef FTM_TEST_MODE_INIT
  FTM2->MODE |= FTM2_MODE_INIT & FTM_MODE_INIT_MASK;
#endif /*FTM_TEST_MODE_INIT*/
  /* Generate the Software Trigger */
  FTM2->SYNC |= FTM_SYNC_SWSYNC_MASK;
/* Enable the FTM counter */
#ifdef FTM2_START_TIMER_IN_INIT
  FTM2->SC |= FTM2_SC_INIT & FTM_SC_CLKS_MASK;
#else
  FTM2->SC &= ~FTM_SC_CLKS_MASK;
#endif
#ifdef FTM2_FMS_INIT
	/* Select enable write protection */
  FTM2->FMS = FTM2_FMS_INIT;
#endif /*FTM2_FMS_INIT*/
}

