/* ADC1 general initialization */
/****************************************************************************/
  /* Software reset ADC1 */
  ADC1->SC1[0] = 0x1FUL;
  ADC1->SC1[1] = 0x1FUL;
  ADC1->CFG1 = 0x0UL;
  ADC1->CFG2 = 0x0UL;
  ADC1->CV1  = 0x0UL;
  ADC1->CV2  = 0x0UL;
  ADC1->SC2  = 0x0UL;
  ADC1->SC3  = 0x0UL;

#if defined(ADC1_CV1_INIT)
  ADC1->CV1 = ADC1_CV1_INIT;
#endif /* ADC1_CV1_INIT */
#if defined(ADC1_CV2_INIT)
  ADC1->CV2 = ADC1_CV2_INIT;
#endif /* ADC1_CV2_INIT */

