  /* Initialize Voltage reference only */
  ADC1->SC2 = (ADC1_SC2_INIT & ADC_SC2_REFSEL_MASK);
#endif /* ADC1_SC2_INIT */
#if defined(ADC1_CFG1_INIT)
  /* Prepare CFG1 register for the best calibration results: ADC frequency <= 4 MHz, clock source = Asynchronous clock */
  ADC1->CFG1 = ((ADC1_CFG1_INIT & (~ADC_CFG1_ADIV_MASK)) | ADC_CFG1_ADICLK_MASK | ADC1_CALIBRATION_PRESCALER_VALUE);
#endif /* ADC1_CFG1_INIT */
#if defined(ADC1_CFG2_INIT)
  /* Additionally enable Asynchronous clock output for lower latency between calibration measurement cycles */
  ADC1->CFG2 = (ADC1_CFG2_INIT | ADC_CFG2_ADACKEN_MASK);
#endif /* ADC1_CFG2_INIT */
