/***********************************************************************************************************************
 * Initialization functions
 **********************************************************************************************************************/
static void BOARD_InitPeripherals_CommonPreInit(void)
{
  /* Enable clock gate of the FTM2 peripheral. */
  SIM->SCGC6 |= SIM_SCGC6_FTM2_MASK;
  /* Enable clock gate of the FTM0 peripheral. */
  SIM->SCGC6 |= SIM_SCGC6_FTM0_MASK;
  /* Enable clock gate of the PDB0 peripheral. */
  SIM->SCGC6 |= SIM_SCGC6_PDB_MASK;
  /* Enable clock gate of the CMP1 peripheral. */
  SIM->SCGC4 |= SIM_SCGC4_CMP_MASK;
  /* Enable clock gate of the ADC0 peripheral. */
  SIM->SCGC6 |= SIM_SCGC6_ADC0_MASK;
  /* Enable clock gate of the ADC1 peripheral. */
  SIM->SCGC6 |= SIM_SCGC6_ADC1_MASK;
}

static void BOARD_InitPeripherals_CommonPostInit(void)
{
  /* Interrupt vector ADC0_IRQn priority settings in the NVIC. */
  NVIC_SetPriority(INT_ADC0_IRQN, INT_ADC0_IRQ_PRIORITY);
  /* Interrupt vector PDB0_IRQn priority settings in the NVIC. */
  NVIC_SetPriority(INT_PDB_IRQN, INT_PDB_IRQ_PRIORITY);
  /* Interrupt vector FTM2_IRQn priority settings in the NVIC. */
  NVIC_SetPriority(INT_FTM2_IRQN, INT_FTM2_IRQ_PRIORITY);
  /* Interrupt vector PORTA_IRQn priority settings in the NVIC. */
  NVIC_SetPriority(INT_GPIO_IRQN, INT_GPIO_IRQ_PRIORITY);
  /* Enable interrupt ADC0_IRQn request in the NVIC. */
  assert(EnableIRQ(INT_ADC0_IRQN) == kStatus_Success);
  /* Enable interrupt PDB0_IRQn request in the NVIC. */
  assert(EnableIRQ(INT_PDB_IRQN) == kStatus_Success);
  /* Enable interrupt FTM2_IRQn request in the NVIC. */
  assert(EnableIRQ(INT_FTM2_IRQN) == kStatus_Success);
  /* Enable interrupt PORTA_IRQn request in the NVIC. */
  assert(EnableIRQ(INT_GPIO_IRQN) == kStatus_Success);
}

void BOARD_InitPeripherals(void)
{
  /* Common pre-initialization */
  BOARD_InitPeripherals_CommonPreInit();
  /* Initialize components */
  UART0_init();
  FTM2_init();
  FTM0_init();
  PDB0_init();
  CMP1_init();
  ADC0_init();
  ADC1_init();
  CMP0_custom_init_init();
  GPIOA_init();
  FreeMASTER_init();
  /* Common post-initialization */
  BOARD_InitPeripherals_CommonPostInit();
}

/***********************************************************************************************************************
 * BOARD_InitBootPeripherals function
 **********************************************************************************************************************/
void BOARD_InitBootPeripherals(void)
{
  BOARD_InitPeripherals();
}
